import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Profile = () => {
  const [showBar, setShowBar] = useState(false);
  const [edit, setEdit] = useState(false);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [address, setAddress] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [gender, setGender] = useState("");
  const [image, setImage] = useState("");

  const showSidebarContent = () => {
    if (showBar === false) {
      const element = document.getElementById("sidebar");
      element.style.width = "15vw";
      const element1 = document.getElementById("profile");
      element1.style.width = "85vw";
      element1.style.marginLeft = "12rem";
      const element2 = document.getElementById("image");
      element2.style.width = "10vw";
      element2.style.height = "10vh";
      const element3 = document.getElementById("icon");
      element3.style.justifyContent = "end";
      setShowBar(true);
      const elements = document.getElementsByClassName("link");
      Array.from(elements).forEach((element) => {
        element.style.display = "block";
      });
    } else {
      const element = document.getElementById("sidebar");
      element.style.width = "5vw";
      const element1 = document.getElementById("profile");
      element1.style.width = "95vw";
      element1.style.marginLeft = "1rem";
      const element2 = document.getElementById("image");
      element2.style.width = "40px";
      element2.style.height = "40px";
      const element3 = document.getElementById("icon");
      element3.style.justifyContent = "center";
      const elements = document.getElementsByClassName("link");
      Array.from(elements).forEach((element) => {
        element.style.display = "none";
      });
      setShowBar(false);
    }
  };

  const editProfile = () => {
    if (edit === false) {
      setEdit(true);
    } else {
      setEdit(false);
    }
  };

  const handleClick = () => {
    editProfile();
    setProfile();
  };

  async function setProfile() {
    try {
      const response = await fetch("http://localhost:5000/user/update_profile", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "x-access-token": localStorage.getItem("token"),
        },
        body: JSON.stringify({
          name: name,
          email: email,
          password: password,
          gender: gender,
          address: address,
          phoneNumber: phoneNumber,
          image: document.getElementById("image"),
        }),
      });

      if(response.ok) {
        alert("Profile Updated")
      } e
    } catch (error) {
      console.error("Network error:", error);
    }
  }

  async function getProfile() {
    try {
      const response = await fetch("http://localhost:5000/user", {
        headers: {
          "x-access-token": localStorage.getItem("token"),
        },
      });

      const data = await response.json();
      if (data) {
        setName(data.user.name || "");
        setEmail(data.user.email || "");
        setPassword(data.user.password);
        setGender(data.user.gender);
        setAddress(data.user.address || "");
        setPhoneNumber(data.user.phoneNumber || "");
        setImage(data.user.image || "");
      }
    } catch (error) {
      console.error("Network error:", error);
    }
  }

  useEffect(() => {
    getProfile();
  }, []);

  return (
    <>
      <section id="sidebar">
        <div className="container">
          <div className="row">
            <div className="col-12 mt-3 mb-3">
              <i
                id="icon"
                class="fa-solid fa-grip-lines text-white"
                onClick={showSidebarContent}
              ></i>
            </div>
            <div className="col-12 mb-3 text-center">
              <img
                src={process.env.PUBLIC_URL + "/images/user-solid.svg"}
                alt="Profile"
                id="image"
                className="rounded-circle"
              />
              <h3>Name</h3>
            </div>
            <div className="col-12 mb-3">
              <ul className="list-unstyled">
                <li>
                  <i class="fa-solid fa-house text-white active">
                    <Link to="/" className="link">
                      Dashboard
                    </Link>
                  </i>
                </li>
                <li>
                  <i class="fa-solid fa-user">
                    <Link to="/" className="link">
                      Profile
                    </Link>
                  </i>
                </li>
                <li>
                  <i class="fa-solid fa-book">
                    <Link to="/" className="link">
                      Booking
                    </Link>
                  </i>
                </li>
              </ul>
            </div>
            <div className="col-12 logout">
              <ul className="text-center list-unstyled">
                <li>
                  <i class="fa-solid fa-arrow-right-from-bracket">
                    <Link to="/" className="link">
                      Logout
                    </Link>
                  </i>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </section>
      <section id="profile">
        <div className="container-fluid">
          <div className={edit ? "row d-none" : "row"}>
            <div className="col-12 p-3 shadow mb-3">
              <h4>Profile</h4>
            </div>
            <div className="col-12 p-3">
              <div className="col-12 d-flex mb-3">
                <div className="col-6">
                  <img
                    src={
                      image || process.env.PUBLIC_URL + "/images/user-solid.svg"
                    }
                    alt="Profile"
                    className="border border-1"
                  />
                </div>
                <div className="col-6 text-end">
                  <button
                    className="btn btn-primary me-5"
                    onClick={editProfile}
                  >
                    <i class="fa-regular fa-pen-to-square me-2"></i>Edit
                  </button>
                </div>
              </div>
              <div className="col-12 d-flex mb-3">
                <div className="col-6 me-2">
                  <p>{name || "No name found"}</p>
                </div>
                <div className="col-6">
                  <p>{email || "No email Found"}</p>
                </div>
              </div>
              <div className="col-12 d-flex mb-3">
                <div className="col-6 me-2">
                  <p>{address || "No address found"}</p>
                </div>
                <div className="col-6">
                  <p>{phoneNumber || "No phone number found"}</p>
                </div>
              </div>
            </div>
          </div>
          <div className={edit ? "row" : "row d-none"}>
            <div className="col-12 p-3 shadow mb-3">
              <h4>Edit Profile</h4>
            </div>
            <div className="col-12 p-3">
              <form action="">
                <div className="col-12 mb-3">
                  <img
                    src={
                      image || process.env.PUBLIC_URL + "/images/user-solid.svg"
                    }
                    alt="Profile"
                    className="border border-1"
                  />
                  <br />
                  <br />
                  <input type="file" className="form-control w-50" id="image" />
                </div>
                <div className="col-12 d-flex mb-3">
                  <div className="col-6 me-2">
                    <input
                      type="text"
                      placeholder={name || "Name"}
                      className="form-control"
                      value={name}
                      onChange={(e) => setName(e.target.value)}
                    />
                  </div>
                  <div className="col-6">
                    <input
                      type="email"
                      placeholder={email || "Email"}
                      className="form-control"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                    />
                  </div>
                </div>
                <div className="col-12 d-flex mb-3">
                  <div className="col-6 me-2">
                    <input
                      type="text"
                      placeholder={address || "Address"}
                      className="form-control"
                      value={address}
                      onChange={(e) => setAddress(e.target.value)}
                    />
                  </div>
                  <div className="col-6">
                    <input
                      type="text"
                      placeholder={phoneNumber || "Phone Number"}
                      className="form-control"
                      value={phoneNumber}
                      onChange={(e) => setPhoneNumber(e.target.value)}
                    />
                  </div>
                </div>
                <div className="col-12 text-center">
                  <button
                    type="submit"
                    className="btn btn-primary"
                    onClick={handleClick}
                  >
                    Update
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default Profile;
