import React, { useState } from "react";
import { Link } from "react-router-dom";

const Sidebar = (props) => {
  const [showBar, setShowBar] = useState(false);

  const showSidebarContent = () => {
    if (showBar === false) {
      const element = document.getElementById("sidebar");
      element.style.width = "20vw";
      const element1 = document.getElementById("icon");
      element1.style.justifyContent = "end";
      setShowBar(true);
    } else {
      const element = document.getElementById("sidebar");
      element.style.width = "5vw";
      const element1 = document.getElementById("icon");
      element1.style.justifyContent = "center";
      setShowBar(false);
    }
  };

  return (
    <>
      <section id="sidebar">
        <div className="container">
          <div className="row">
            <div className="col-12 mt-3 mb-3">
              <i
                id="icon"
                class="fa-solid fa-grip-lines text-white"
                onClick={showSidebarContent}
              ></i>
            </div>
            <div className="col-12 mb-3">
              <img src="" alt="" />
            </div>
            <div className="col-12 mb-3">
              <ul className="text-center list-unstyled">
                <li className="active">
                  <i class="fa-solid fa-house text-primary">
                    <Link to="/">Dashboard</Link>
                  </i>
                </li>
                <li>
                  <i class="fa-solid fa-user">
                    <Link to="/">Profile</Link>
                  </i>
                </li>
                <li>
                  <i class="fa-solid fa-book">
                    <Link to="/">Booking</Link>
                  </i>
                </li>
              </ul>
            </div>
            <div className="col-12 logout d-flex align-items-end flex-column">
              <ul className="text-center list-unstyled mt-auto">
                <li>
                  <i class="fa-solid fa-arrow-right-from-bracket">
                    <Link to="/">Logout</Link>
                  </i>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default Sidebar;
