import React, { useState } from "react";
import { Link } from "react-router-dom";

const Footer = () => {
  const [isHovered1, setIsHovered1] = useState(false);
  const [isHovered2, setIsHovered2] = useState(false);
  const [isHovered3, setIsHovered3] = useState(false);
  const [isHovered4, setIsHovered4] = useState(false);
  const [isHovered5, setIsHovered5] = useState(false);
  const [isHovered6, setIsHovered6] = useState(false);

  const handleMouseEnter1 = () => {
    setIsHovered1(true);
  };

  const handleMouseLeave1 = () => {
    setIsHovered1(false);
  };

  const handleMouseEnter2 = () => {
    setIsHovered2(true);
  };

  const handleMouseLeave2 = () => {
    setIsHovered2(false);
  };

  const handleMouseEnter3 = () => {
    setIsHovered3(true);
  };

  const handleMouseLeave3 = () => {
    setIsHovered3(false);
  };

  const handleMouseEnter4 = () => {
    setIsHovered4(true);
  };

  const handleMouseLeave4 = () => {
    setIsHovered4(false);
  };

  const handleMouseEnter5 = () => {
    setIsHovered5(true);
  };

  const handleMouseLeave5 = () => {
    setIsHovered5(false);
  };

  const handleMouseEnter6 = () => {
    setIsHovered6(true);
  };

  const handleMouseLeave6 = () => {
    setIsHovered6(false);
  };

  return (
    <>
      <section id="footer">
        <div className="container-fluid bg-primary p-5">
          <div class="row d-flex">
            <div class="col-5 text-center me-3">
              <a
                class="d-flex justify-content-center text-decoration-none mb-3"
                href="#home"
              >
                <img
                  src="/images/alt-logo.png"
                  alt="Alternate Logo"
                  class="logo"
                />
                <h4 class="d-flex justify-content-center align-items-center text-white mt-2">
                  Hotel <br /> Haven
                </h4>
              </a>
              <p className="text-white">
                Discover an all-in-one hotel experience on our webpage: a
                seamless blend of accommodation, a powerful to-do list with
                reminders, a journal with calendar integration, and a secure
                password manager with hash decryption. Stay organized, reflect
                on cherished memories, and safeguard your personal information
                during your unforgettable stay. Book now and enjoy a
                comprehensive and convenient solution for productivity,
                reflection, and security at our hotel.
              </p>
              <div class="links">
                <a href="/" class="text-white ms-3">
                  <i class="fa-brands fa-facebook"></i>
                </a>
                <a href="/" class="text-white ms-3">
                  <i class="fa-brands fa-instagram"></i>
                </a>
                <a href="/" class="text-white ms-3">
                  <i class="fa-brands fa-twitter"></i>
                </a>
                <a href="/" class="text-white ms-3">
                  <i class="fa-solid fa-envelope"></i>
                </a>
              </div>
            </div>
            <div class="col-2 mb-lg-0 me-3">
              <h2 className="text-white">Links</h2>
              <ul class="list-unstyled">
                <li
                  className="d-flex"
                  onMouseEnter={handleMouseEnter1}
                  onMouseLeave={handleMouseLeave1}
                >
                  <img
                    src={
                      isHovered1
                        ? process.env.PUBLIC_URL + "/icons/right-arrow.gif"
                        : process.env.PUBLIC_URL + "/icons/right-arrow.png"
                    }
                    alt=""
                    className="me-2"
                  />
                  <Link to="#home" class="text-decoration-none text-secondary">
                    Home
                  </Link>
                </li>
                <li
                  className="d-flex"
                  onMouseEnter={handleMouseEnter2}
                  onMouseLeave={handleMouseLeave2}
                >
                  <img
                    src={
                      isHovered2
                        ? process.env.PUBLIC_URL + "/icons/right-arrow.gif"
                        : process.env.PUBLIC_URL + "/icons/right-arrow.png"
                    }
                    alt=""
                    className="me-2"
                  />
                  <Link to="#about" class="text-decoration-none text-secondary">
                    About
                  </Link>
                </li>
                <li
                  className="d-flex"
                  onMouseEnter={handleMouseEnter3}
                  onMouseLeave={handleMouseLeave3}
                >
                  <img
                    src={
                      isHovered3
                        ? process.env.PUBLIC_URL + "/icons/right-arrow.gif"
                        : process.env.PUBLIC_URL + "/icons/right-arrow.png"
                    }
                    alt=""
                    className="me-2"
                  />
                  <Link to="#room" class="text-decoration-none text-secondary">
                    Rooms
                  </Link>
                </li>
                <li
                  className="d-flex"
                  onMouseEnter={handleMouseEnter4}
                  onMouseLeave={handleMouseLeave4}
                >
                  <img
                    src={
                      isHovered4
                        ? process.env.PUBLIC_URL + "/icons/right-arrow.gif"
                        : process.env.PUBLIC_URL + "/icons/right-arrow.png"
                    }
                    alt=""
                    className="me-2"
                  />
                  <Link to="#services" class="text-decoration-none text-secondary">
                    Services
                  </Link>
                </li>
                <li
                  className="d-flex"
                  onMouseEnter={handleMouseEnter5}
                  onMouseLeave={handleMouseLeave5}
                >
                  <img
                    src={
                      isHovered5
                        ? process.env.PUBLIC_URL + "/icons/right-arrow.gif"
                        : process.env.PUBLIC_URL + "/icons/right-arrow.png"
                    }
                    alt=""
                    className="me-2"
                  />
                  <Link to="#comment" class="text-decoration-none text-secondary">
                    Comments
                  </Link>
                </li>
                <li
                  className="d-flex"
                  onMouseEnter={handleMouseEnter6}
                  onMouseLeave={handleMouseLeave6}
                >
                  <img
                    src={
                      isHovered6
                        ? process.env.PUBLIC_URL + "/icons/right-arrow.gif"
                        : process.env.PUBLIC_URL + "/icons/right-arrow.png"
                    }
                    alt=""
                    className="me-2"
                  />
                  <Link to="/login" class=" fstext-decoration-none text-secondary">
                    Login
                  </Link>
                </li>
              </ul>
            </div>
            <div class="col-4 text-center">
              <h3 class="mb-3">Sign Up for Our Newsletter</h3>
              <form action="">
                <div class="form-floating mb-3">
                  <input
                    type="email"
                    class="form-control"
                    id="floatingInput"
                    placeholder="Enter Email Address"
                  />
                  <label for="floatingInput">Email Address</label>
                </div>
                <div>
                  <button
                    type="submit"
                    class="border border-2 border-secondary rounded-pill p-3 bg-primary text-secondary fw-bold"
                  >
                    Sign Up
                  </button>
                </div>
              </form>
            </div>
            <hr class="my-3" />
            <div class="col-12 d-flex justify-content-center">
              <p>
                Â© All Rights Reserved by Chronicle Keeper. Powered by Mustafa
                Nadeem
              </p>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default Footer;
