import React, { useState } from "react";

const Sidebar = (props) => {
  const [showBar, setShowBar] = useState(false);

  const showSidebarContent = () => {
    if (showBar === false) {
      const element = document.getElementById("sidebar");
      element.style.width = "20vw";
      const element1 = document.getElementById("icon");
      element1.style.justifyContent = "end";
      setShowBar(true);
    } else {
      const element = document.getElementById("sidebar");
      element.style.width = "5vw";
      const element1 = document.getElementById("icon");
      element1.style.justifyContent = "center";
      setShowBar(false);
    }
  };

  return (
    <>
      <section id="sidebar">
        <div className="container">
          <div className="row">
            <div className="col mt-3 mb-3">
              <i
                id="icon"
                class="fa-solid fa-grip-lines text-white"
                onClick={showSidebarContent}
              ></i>
            </div>
            <div className="col">
                <img src="" alt="" />
            </div>
            <div className="col">
                <ul>
                    <li><a href="/">Dashboard</a></li>
                    <li><a href="">Pro</a></li>
                </ul>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default Sidebar;
