import React, { useState } from "react";
import { Link } from "react-router-dom";

const Sidebar = (props) => {
  const [showBar, setShowBar] = useState(false);

  const showSidebarContent = () => {
    if (showBar === false) {
      const element = document.getElementById("sidebar");
      element.style.width = "15vw";
      const element1 = document.getElementById("icon");
      element1.style.justifyContent = "end";
      setShowBar(true);
      const elements = document.getElementsByClassName("link");
      Array.from(elements).forEach((element) => {
        element.style.display = "block";
      });
    } else {
      const element = document.getElementById("sidebar");
      element.style.width = "5vw";
      const element1 = document.getElementById("icon");
      element1.style.justifyContent = "center";
      const elements = document.getElementsByClassName("link");
      Array.from(elements).forEach((element) => {
        element.style.display = "none";
      });
      setShowBar(false);
    }
  };

  return (
    <>
      <section id="sidebar">
        <div className="container">
          <div className="row">
            <div className="col-12 mt-3 mb-3">
              <i
                id="icon"
                class="fa-solid fa-grip-lines text-white"
                onClick={showSidebarContent}
              ></i>
            </div>
            <div className="col-12 mb-3">
              <img src={process.env.PUBLIC_URL + "/images"} alt="" />
            </div>
            <div className="col-12 mb-3">
              <ul className="list-unstyled">
                <li>
                  <i class="fa-solid fa-house text-white active">
                    <Link to="/" className="link">
                      Dashboard
                    </Link>
                  </i>
                </li>
                <li>
                  <i class="fa-solid fa-user">
                    <Link to="/" className="link">
                      Profile
                    </Link>
                  </i>
                </li>
                <li>
                  <i class="fa-solid fa-book">
                    <Link to="/" className="link">
                      Booking
                    </Link>
                  </i>
                </li>
              </ul>
            </div>
            <div className="col-12 logout">
              <ul className="text-center list-unstyled">
                <li>
                  <i class="fa-solid fa-arrow-right-from-bracket">
                    <Link to="/" className="link">
                      Logout
                    </Link>
                  </i>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default Sidebar;
