import React, { useEffect, useState } from "react";
import Sidebar from "../components/Sidebar";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const navigate = useNavigate();
  const [serverData, setServerData] = useState("");
  const [bookingData, setBookingData] = useState("");
  const [comment, setComment] = useState("");
  const [stars, setStars] = useState("");

  async function getUser() {
    try {
      const response = await fetch("http://localhost:5000/user", {
        headers: {
          "x-access-token": localStorage.getItem("token"),
        },
      });

      const data = await response.json();
      if (data) {
        setServerData(data.user);
      } else {
        alert("Error Finding User");
      }
    } catch (error) {
      console.error("Network error:", error);
    }
  }

  async function getBooking() {
    try {
      const response = await fetch("http://localhost:5000/room/get_bookings", {
        headers: {
          "x-access-token": localStorage.getItem("token"),
        },
      });

      const data = await response.json();
      if (data) {
        setBookingData(data.bookings);
        console.log(data);
      } else {
        alert("Error Finding Booking");
      }
    } catch (error) {
      console.error("Network error:", error);
    }
  }

  async function addComment() {
    try {
      const response = await fetch("http://localhost:5000/comment", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          name: serverData.name,
          comment: comment,
          stars: stars,
        }),
      });

      const data = await response.json();
      if (data) {
        console.log(data);
      } else {
        alert("Error" + response);
        console.log(response);
      }
    } catch (error) {
      console.log(error);
    }
  }

  const handleRatingChange = (event) => {
    const selectedRating = event.target.value;
    setStars(selectedRating);
  };

  useEffect(() => {
    getUser();
    getBooking();
  }, []);

  const handleDelete = (roomID) => {
    async function deleteBooking() {
      try {
        const response = await fetch(
          "http://localhost:5000/room/delete_booking",
          {
            method: "DELETE",
            headers: {
              "Content-Type": "application/json",
              "x-access-token": localStorage.getItem("token"),
            },
            body: JSON.stringify({
              roomID: roomID,
            }),
          }
        );

        await response.json();
        if (response.ok) {
          navigate("/dashboard");
        } else {
          alert("Booking not found");
        }
      } catch (error) {
        console.error("Network error:", error);
      }
    }

    deleteBooking();
  };

  return (
    <>
      <Sidebar
        icon1="fa-solid fa-home text-white active"
        link1="Dashboard"
        path1="/dashboard"
        icon2="fa-solid fa-user"
        link2="Profile"
        path2="/profile"
        icon3="fa-solid fa-book"
        link3="Booking"
        path3="/booking"
      />
      <section id="content">
        <div className="container-fluid">
          <div className="row">
            <div className="col-12 p-3 shadow mb-3">
              <h4>Dashboard</h4>
            </div>
            <div className="col-12 p-3 mb-3">
              <h4 className="fw-bold text-primary">Bookings</h4>
              {bookingData && bookingData.length > 0 ? (
                bookingData.map((booking, index) => (
                  <div key={index} className="border border-2 rounded p-3">
                    <div className="mb">
                      <div className="d-flex justify-content-between">
                        <h6>Booking Details:</h6>
                        <button
                          className="border-0 bg-white text-primary text-decoration-underline"
                          title="Delete"
                          onClick={() => handleDelete(booking.roomID)}
                        >
                          Cancel
                        </button>
                      </div>
                      <div className="d-flex">
                        <p>Date: </p>
                        <p className="ms-5">From: {booking.checkInDate}</p>
                        <p className="ms-5">To: {booking.checkOutDate}</p>
                      </div>
                      <p>Room Details: </p>
                      <div className="d-flex">
                        <p>Number: {booking.roomID}</p>
                        <p className="ms-5">Type: {booking.roomType}</p>
                      </div>
                      <div className="d-flex">
                        <p>Adults: {booking.adults}</p>
                        <p className="ms-5">Children: {booking.children}</p>
                      </div>
                      <div className="d-flex">
                        <p className="ms-auto">
                          <span className="fw-bold">Total Price:</span> Rs.
                          {booking.price}
                        </p>
                      </div>
                    </div>
                  </div>
                ))
              ) : (
                <p>No Data Found</p>
              )}
            </div>
            <div className="col-12 p-3 d-flex justify-content-center align-items-center">
              <div className="col-6 text-center">
                <h4 className="mb-3">Leave A Comment</h4>
                <form onSubmit={addComment()}>
                  <div class="form-floating mb-3">
                    <textarea
                      class="form-control"
                      placeholder="Leave a comment here"
                      id="customerComment"
                      value={comment}
                      onChange={(e) => setComment(e.target.value)}
                    ></textarea>
                    <label for="customerComment">Comment</label>
                  </div>
                  <div class="rating fs-2 mb-3">
                    <input
                      type="radio"
                      id="star5"
                      value="5"
                      onChange={handleRatingChange}
                    />
                    <label for="star5" title="Excellent"></label>
                    <input
                      type="radio"
                      id="star4"
                      value="4"
                      onChange={handleRatingChange}
                    />
                    <label for="star4" title="Very Good"></label>
                    <input
                      type="radio"
                      id="star3"
                      value="3"
                      onChange={handleRatingChange}
                    />
                    <label for="star3" title="Good"></label>
                    <input
                      type="radio"
                      id="star2"
                      value="2"
                      onChange={handleRatingChange}
                    />
                    <label for="star2" title="Fair"></label>
                    <input
                      type="radio"
                      id="star1"
                      value="1"
                      onChange={handleRatingChange}
                    />
                    <label for="star1" title="Very Poor"></label>
                  </div>
                  <div class="text-center">
                    <button type="submit" class="btn btn-primary">
                      Submit
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default Dashboard;
